                            <textarea class="form-control" rows="5" id="stComentario" name="stComentario"></textarea>
                        </div>
                    </div>
                </div>
                <div class="modal-footer">
                    <button type="button" id="btnGuardarComentario" (click)="guardarComentario()"
                        class="btn btn-primary">Guardar</button>
                    <button type="button" class="btn btn-danger" (click)="closePopup()">
                        Cerrar
                    </button>
                </div>
            </div>
        </div>
    </div>
</form>
<h4 mat-dialog-title class="dialog-headline">
  <mat-toolbar role="toolbar" class="dialog-header">
    <span>{{data.stCodigo}}</span>
    <span class="fx-spacer"></span>
    <button mat-icon-button (click)="onNoClick()">
      <mat-icon mat-list-icon>close</mat-icon>
    </button>
  </mat-toolbar>
</h4>
<div mat-dialog-content>
  <br>
  <div class="container-fluid">
    <div class="form-horizontal">
      <div class="row">
        <div class="col-md-12">
          <mat-form-field appearance="outline">
            <mat-label>Motivo de rechazo</mat-label>
            <textarea matInput placeholder="Motivo de rechazo" matInput [(ngModel)]="data.stMotivo" rows="3"></textarea>
          </mat-form-field>
        </div>
      </div>
      <br>
      <div class="row">
        <div class="col-md-12 text-right">
          <button mat-raised-button class="btn btn-primary" [mat-dialog-close]="data.stMotivo||'NO_INGRESÓ'"
            cdkFocusInitial>
            <mat-icon>check</mat-icon>
            Aceptar
          </button>
          <button mat-raised-button class="btn btn-info" (click)="onNoClick()">
            <mat-icon>block</mat-icon>
            Cancelar
          </button>
        </div>
      </div>
      <br>
    </div>
  </div>
</div>
